def count_complete_binary_trees(n):
    if n == 0:
        return 1
    if n == 1:
        return 1
    dp = [0] * (n+1)
    dp[0] = 1
    dp[1] = 1
    for i in range(2, n+1):
        dp[i] = sum(dp[j] * dp[i-1-j] for j in range(i))
    return dp[n]

